<template>
	<view class="t-sbutton"
	      :style="{top: top, backgroundColor: bgColor||defaultColor, [positon]: `${offset}rpx`, transform: `rotate(${positon==='right'?'0deg':'180deg'})`}">
		<view class="t-sbutton-icon t-sbutton-icon-more t-sbutton__more" :style="{color: fontColor }"
					v-if="isMore" @click="more"></view>
		<view class="t-sbutton__content" :style="{backgroundColor: bgColor||defaultColor }" v-else="!isMore">
			<slot></slot>
		</view>
	</view>
</template>

<script>
	export default {
		name: "t-sbutton",
		props: {
			bgColor: {
				type: String,
				default: ''
			},
			fontColor: {
				type: String,
				default: '#FFF'
			},
			top: {
				type: String,
				default: '50%'
			},
			positon: {
				type: String,
				default: 'right'
			}
		},
		data() {
			return {
				defaultColor: '',
				isMore: true,
				offset: -80,
				defaultOffset: -80,
				timer: null,
			};
		},
		created() {
			this.defaultColor = this.$it.color.primary
		},
		destroyed() {
			clearTimeout(this.timer)
		},
		methods:{
			more() {
				this.offset = 0
				this.isMore = false
				this.timer = setTimeout(()=>{
					this.offset = this.defaultOffset
					this.isMore = true
				},5000)
			}
		}
	}
</script>

<style lang="scss">
	@font-face {
		font-family: "t-sbutton-icon";
		src: url('data:application/x-font-woff2;charset=utf-8;base64,') format('woff2');
	}

	.t-sbutton-icon {
		font-family: "t-sbutton-icon" !important;
		font-size: 16px;
		font-style: normal;
		-webkit-font-smoothing: antialiased;
		-moz-osx-font-smoothing: grayscale;
	}

	.t-sbutton-icon-more:before {
		content: "\e635";
	}

	.t-sbutton {
		position: fixed;
		width: 130rpx;
		height: 100rpx;
		z-index: 10;
		border-radius: 50rpx 0 0 50rpx;

		&__more {
			position: absolute;
			line-height: 100rpx;
			left: 8rpx;
			top: 0;
			font-size: 40rpx;
		}

		&__content {
			position: absolute;
			left: 0;
			top: 0;
			width: 100rpx;
			height: 100rpx;
			border-radius: 50rpx;
			display: flex;
			align-items: center;
			justify-content: center;
			box-shadow: 0 0 20rpx 0rpx rgba(0, 0, 0, 0.1);
			transition: transform 0.4s;
			transform: translateX(0) rotate(0deg);
			color: #FFFFFF;
		}
	}
</style>
